CREATE TABLE "public"."inventory_products" ("inventory_id" uuid NOT NULL, "product_id" uuid NOT NULL, "quantity" integer NOT NULL DEFAULT 1, PRIMARY KEY ("inventory_id","product_id") , FOREIGN KEY ("inventory_id") REFERENCES "public"."inventories"("id") ON UPDATE restrict ON DELETE cascade, FOREIGN KEY ("product_id") REFERENCES "public"."products"("id") ON UPDATE restrict ON DELETE cascade, CONSTRAINT "possible_quantity" CHECK (quantity > 0));COMMENT ON TABLE "public"."inventory_products" IS E'This table tracks the availability of products in an inventory.';
